sys_ux_client_script.script:
/**
  * @param {params} params
  * @param {api} params.api
  * @param {TransformApiHelpers} params.helpers
  */
function evaluateProperty({api, helpers}) {
  var metricName = api.item.value.metric_type_tiny_name;
  var color = "65b3036ca9fe3dba0197ee0f7e51375c";
  if (metricName == "system_uptime_sec") {
    color = "c4d8ba9fb5e88110f877767cc85d6488";
  } else if (metricName === "process_status") {
    color = "0bb157b2a357111004b9781346fcda30";
  }

	return {
    color: color,
    metric: "YldWMGNtbGpRbUZ6WldOdFpHSmZZMmxmZDJsdVgzTmxjblpsY2pFM01EVTJNek0wTURrM056ST0xNzA1NjM0NDc4NTgx"
  };
}
